import{_ as e,c as t,o as r,ag as o}from"./chunks/framework.B4sg4S7y.js";const d=JSON.parse('{"title":"XSS攻击","description":"","frontmatter":{},"headers":[],"relativePath":"pages/网络/渡一/18. XSS攻击/课件.md","filePath":"pages/网络/渡一/18. XSS攻击/课件.md"}'),i={name:"pages/网络/渡一/18. XSS攻击/课件.md"};function s(l,a,n,p,S,c){return r(),t("div",null,[...a[0]||(a[0]=[o('<h1 id="xss攻击" tabindex="-1">XSS攻击 <a class="header-anchor" href="#xss攻击" aria-label="Permalink to &quot;XSS攻击&quot;">​</a></h1><p>XSS（Cross Site Scripting，跨站脚本攻击），是指攻击者利用站点的漏洞，在表单提交时，在表单内容中加入一些恶意脚本，当其他正常用户浏览页面，而页面中刚好出现攻击者的恶意脚本时，脚本被执行，从而使得页面遭到破坏，或者用户信息被窃取。</p><img src="http://mdrs.yuanjin.tech/img/20211102135438.png" alt="image-20211102135438219" style="zoom:50%;" align="left"><h1 id="防御方式" tabindex="-1">防御方式 <a class="header-anchor" href="#防御方式" aria-label="Permalink to &quot;防御方式&quot;">​</a></h1><p>服务器端对用户提交的内容进行过滤或编码</p><ul><li>过滤：去掉一些危险的标签，去掉一些危险的属性</li><li>编码：对危险的标签进行HTML实体编码</li></ul><h1 id="面试题" tabindex="-1">面试题 <a class="header-anchor" href="#面试题" aria-label="Permalink to &quot;面试题&quot;">​</a></h1><p>介绍 XSS 攻击</p><blockquote><p>参考答案：</p><p>XSS 是指跨站脚本攻击。攻击者利用站点的漏洞，在表单提交时，在表单内容中加入一些恶意脚本，当其他正常用户浏览页面，而页面中刚好出现攻击者的恶意脚本时，脚本被执行，从而使得页面遭到破坏，或者用户信息被窃取。</p><p>要防范 XSS 攻击，需要在服务器端过滤脚本代码，将一些危险的元素和属性去掉或对元素进行HTML实体编码。</p></blockquote>',9)])])}const h=e(i,[["render",s]]);export{d as __pageData,h as default};
